import { useState, useEffect } from 'react';
import SingleDisplay from "./SingleDisplay.js";



const CountriesDisplay = (props) => {

  const [getDisplay, setDisplay] = useState('Please enter a country name')
  const countries = props.countries;


  const handleOneCountryDisplay = (countryObj) => {
    singleShow = <SingleDisplay countryObject={countryObj} />;
  }

  let response = countries.map((countryObject, index) => {
    return <li key={index}>{countryObject.name.common}<button onClick={() => { handleOneCountryDisplay(countryObject) }}>More</button></li>
  })

  if (singleShow) {
    return singleShow
  }

  if (response.length > 10) {
    return (
      <span>Too many matches, please be more specific.</span>
    );
  }
  if (response.length === 0) {
    return <span>No matches found</span>
  }
  return (
    <>
      {response}
    </>
  )
}

export default CountriesDisplay;
