import { useState, useEffect } from 'react';
import axios from 'axios';

const WeatherDisplay = (props) => {
  const [getCoordinates, setCoordinates] = useState(props.coordinates);
  const [getWeatherData, setWeatherData] = useState('');
  const [getImage, setImage] = useState(<p>No matching image found</p>);
  const [getDescription, setDescription] = useState(<p>Hmm, today is a mystery</p>)
  const [getApiKey, setApikey] = useState(process.env.REACT_APP_API_KEY)

  // OpenWeather API KEY
  // IMPORTANT! DO NOT OVERWRITE! Set as env variable instead!
  // In bash: REACT_APP_API_KEY="VALUEHERE" && NPM START


  useEffect(() => {
    axios.get(`https://api.openweathermap.org/data/2.5/weather?lat=${getCoordinates[0]}&lon=${getCoordinates[1]}&appid=${getApiKey}`).then(response => {
      setWeatherData(response.data)
      console.log(`INIT got data like ${getWeatherData}`)
    })
  }, [])



  useEffect(() => {
    axios.get(`https://api.openweathermap.org/data/2.5/weather?lat=${getCoordinates[0]}&lon=${getCoordinates[1]}&appid=${getApiKey}`).then(response => {
      setWeatherData(response.data);
      console.log(`got data like ${getWeatherData}`)
    })
  }, [getCoordinates])

  useEffect(() => {
    const iconURL = getWeatherData.weather[0].icon
    const desc = getWeatherData.weather[0].description;
    setImage(<img src={`https://openweathermap.org/img/wn/${iconURL}@2x.png`} />)
    setDescription(<p>Today is looking like a {desc} type of day.</p>)
  }, [getWeatherData])


  const kevinToCelcius = (kelvins) => {
    return (kelvins - 273.5).toFixed(2);
  }

  const ShowLatitudes = (props) => {
    const coordArray = props.coordArray;
    return (
      <h4>Latitude is {coordArray[0]} and longiitude is {coordArray[1]}</h4>
    )

  }

  // <p>The current temperature is {kevinToCelcius(getWeatherData.main.temp)}°</p>
  return (
    <>
      <>
        {<ShowLatitudes coordArray={getCoordinates} />}
      </>
      <>
      </>
      <>
        {getImage}
      </>
      <>
        {getDescription}
      </>
    </>
  );
};

export default WeatherDisplay;
