import { useState, useEffect } from 'react';
import axios from 'axios';

function App() {


  const [getNotes, setNotes] = useState([])
  const [getAllNotes, setAllNotes] = useState([])
  const [getFilter, setFilter] = useState('')


  const handleOnInputChange = (event) => {
    const userInput = event.target.value;
    setFilter(userInput)
    if (getFilter != '') {
      const filteredCountries = getAllNotes.filter((countryObject) => {
        countryObject.name.common.toLowerCase().startsWith(getFilter.toLowerCase);
      })
      setNotes(filteredCountries)
    }
    setNotes(getAllNotes)
  }


  useEffect(() => {
    axios.get(`https://restcountries.com/v3.1/all`).then(
      (response) => {
        console.log('used effect has triggered.')
        setNotes(response.data);
        setAllNotes(response.data);
      }
    );
  }, []);

  return (
    <>
      <>
        <h1>Country Information App</h1>
      </>
      <>
        <h2>Find your favorite country here!</h2>
      </>
      <>
        <input onChange={handleOnInputChange}></input>
      </>

      <>
        <ul>
          {getNotes.map((countryObject, index) => {
            return <li key={index}>{countryObject.name.common}</li>;
          })}
        </ul>
      </>
    </>
  )
}



export default App;
