import { useState, useEffect } from 'react';
import axios from 'axios';

const WeatherDisplay = (props) => {
  const [getCoordinates, setCoordinates] = useState(props.coordinates)
  const [getWeatherData, setWeatherData] = useState('')



  // OpenWeather API KEY
  // IMPORTANT! DO NOT OVERWRITE! Set as env variable instead!
  // In bash: REACT_APP_API_KEY="VALUEHERE" && NPM START
  const APIKey = process.env.REACT_APP_API_KEY

  useEffect(() => {
    setCoordinates(props.coordinates)

  }, [props])

  useEffect(() => {
    axios.get(`https://api.openweathermap.org/data/2.5/weather?lat=${getCoordinates[0]}&lon=${getCoordinates[0]}&appid=${APIKey}`).then((response) => {
      setWeatherData(response.data);
      console(`called API with response ${getWeatherData}`)
    })
  }, [getCoordinates])

  return (
    <>
      <>
        <h4>The latitude is {getCoordinates[0]} and the longitude is {getCoordinates[1]}</h4>
      </>
      <>
        <p>Current temperature is ${getWeatherData.main.temp}</p>
      </>
    </>
  );
};



export default WeatherDisplay;
