import { useState } from 'react'
import Note from './components/Note'
import axios from 'axios';

let App = (props) => {


  let notes = props.notes;

  let [getAllNotes, setAllNotes] = useState(notes);
  let [getNewNote, setNewNote] = useState('');
  let [showAll, setShowAll] = useState(true)


  const notesToShow =
    showAll ? getAllNotes : getAllNotes.filter(note => (note.important));


  let handleNewNoteChange = (event) => {
    let newNote = event.target.value;
    setNewNote(newNote);
  }


  const toggleImportance = (id) => {
    const getURL = `http://localhost:3001/notes/${id}`
    const oldNote = getAllNotes.find((note) => note.id === id)
    const newNote = { ...oldNote, important: !oldNote.important };
    axios.put(getURL, newNote);
    setAllNotes(getAllNotes.map((note) => note.id != id ? note : newNote))

  }

  let handleSaveNewNote = (event) => {
    event.preventDefault();
    let userInput = getNewNote
    let newNoteObject = {
      content: userInput,
      important: Math.random() > 0.5,
      date: new Date(),
    };

    axios.post(`http://localhost:3001/notes`, newNoteObject).then((response) => {
      console.log(response.data);
      setAllNotes(getAllNotes.concat(response.data));
      setNewNote('')
    })
  }
  /* // Changed in favour of Axios post.
  /* // Changed in favour of Axios post.
 setAllNotes(getAllNotes.concat(newNoteObject));
    setNewNote("");

  */


  return (
    <>
      <>
        <h1>Note-Taking App</h1>
        <ul>
          {notesToShow.map((note) => <li key={note.id}><Note note={note} toggle={() => { toggleImportance(note.id) }} /></li>)}
        </ul>
        <>
          <button onClick={() => setShowAll(!showAll)}>
            {showAll ? 'Important' : 'Everything'}
          </button>
        </>
      </>
      <>
        <h2>Add a new Note!</h2>
        <form onSubmit={handleSaveNewNote}>
          <input onChange={handleNewNoteChange} value={getNewNote} />
          <button type="submit">Save!</button>
        </form>
      </>
    </>
  )
}
export default App;
