import { useState } from 'react'
import Note from './components/Note'

const App = ({ notes }) => {
  let [getNotes, setNotes] = useState(notes);
  let [getNewNote, setNewNote] = useState('')

  let handleNewNoteClick = (event) => {
    let newNoteObject = {
      content: getNewNote,
      date: new Date().toISOString,
      importante: Math.random > 0.49,
      id: getNotes.length + 1,
    };
    setNewNote(newNoteObject);
    setNotes(getNotes.concat(newNoteObject));
    setNewNote('');
    return;
  }
  let handleNoteChange = (event) => {
    setNewNote(event.target.value);
    console.log(getNewNote);
  }
  return (
    <>
      <>
        <h1>My Notes</h1>
        {getNotes.map((note) => {
          return <Note note={note} key={note.id} />
        }
        )}
      </>
      <>
        <form onSubmit={handleNewNoteClick}>
          <input value={getNewNote} onChange={handleNoteChange} />
          <button type='submit'>Save!</button>
        </form>
      </>
    </>
  )
};

export default App;
